use std::collections::HashMap;
extern crate vec_map;
use vec_map::VecMap;

macro_rules! vecmap {
    ($( $key: expr => $val: expr ),*) => {{
        let mut map = VecMap::new();
        $( map.insert($key as usize, $val ); )*
        map
    }}
}

